import axios from 'axios';

// Create axios instance
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',
  timeout: 10000,
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Token expired or invalid
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  register: (userData) => api.post('/auth/register', userData),
  login: (credentials) => api.post('/auth/login', credentials),
  logout: () => api.post('/auth/logout'),
  getMe: () => api.get('/auth/me'),
};

// User API
export const userAPI = {
  getAllUsers: (params) => api.get('/users', { params }),
  createUser: (userData) => api.post('/users', userData),
  updateProfile: (userData) => {
    console.log('ðŸ”§ API: Updating profile', userData);
    return api.patch('/users/profile', userData);
  },
  getDashboardStats: () => api.get('/users/dashboard-stats'),
};

// Admin API
export const adminAPI = {
  // User management
  updateUser: (userId, userData) => {
    console.log('ðŸ”§ API: Updating user', userId, userData);
    return api.patch(`/admin/users/${userId}`, userData);
  },
  deleteUser: (userId) => {
    console.log('ðŸ—‘ï¸  API: Deleting user', userId);
    return api.delete(`/admin/users/${userId}`);
  },
  
  // Store management
  updateStore: (storeId, storeData) => {
    console.log('ðŸ”§ API: Updating store', storeId, storeData);
    return api.patch(`/admin/stores/${storeId}`, storeData);
  },
  deleteStore: (storeId) => {
    console.log('ðŸ—‘ï¸  API: Deleting store', storeId);
    return api.delete(`/admin/stores/${storeId}`);
  },
};

// Store API
export const storeAPI = {
  getAllStores: (params) => api.get('/stores', { params }),
  createStore: (storeData) => api.post('/stores', storeData),
  createOwnStore: (storeData) => api.post('/stores/create-own', storeData),
  getMyStores: () => api.get('/stores/my-stores'),
  getStoreRatings: (storeId) => api.get(`/stores/${storeId}/ratings`),
  updateStoreByOwner: (storeId, storeData) => api.patch(`/stores/${storeId}/update`, storeData),
};

// Rating API
export const ratingAPI = {
  submitRating: (ratingData) => api.post('/ratings', ratingData),
  getUserRatings: () => api.get('/ratings/my-ratings'),
};

export default api;
