import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle auth errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);
export const adminAPI = {
  updateUser: (userId, userData) => api.patch(`/admin/users/${userId}`, userData),
  updateStore: (storeId, storeData) => api.patch(`/admin/stores/${storeId}`, storeData),
};
// User API
export const userAPI = {
  getDashboardStats: () => api.get('/users/dashboard-stats'),
  getAllUsers: (params = {}) => api.get('/users', { params }),
  createUser: (userData) => api.post('/users', userData),
  updateProfile: (userData) => api.patch('/users/profile', userData),
};

// Store API
export const storeAPI = {
  getAllStores: (params = {}) => api.get('/stores', { params }),
  createStore: (storeData) => api.post('/stores', storeData), // For admin
  createOwnStore: (storeData) => api.post('/stores/create-own', storeData), // For store owner
  getMyStores: () => api.get('/stores/my-stores'),
  getStoreRatings: (storeId) => api.get(`/stores/${storeId}/ratings`),
  updateStoreByOwner: (storeId, storeData) => api.patch(`/stores/${storeId}/update`, storeData),

};

// Rating API
export const ratingAPI = {
  submitRating: (ratingData) => api.post('/ratings', ratingData),
  getMyRatings: () => api.get('/ratings/my-ratings'),
};

export default api;
